import { Sidebar, Menu, MenuItem, useProSidebar } from 'react-pro-sidebar';
import React, {useState, setState} from 'react';
import axios from 'axios';


const Layout = () => {
    const { collapseSidebar } = useProSidebar();
    const [chartsByNamespace, setChartsByNamespace] = useState(null);
    const [isLoading, setLoading] = useState(true);

    let namespaceList;

    useEffect(() => {
      axios.get('http://localhost:8080/api/v1/helm/namespaces')
      .then(response => {
        setChartsByNamespace(response.data["releases"]);
        console.log(response.data["releases"]);
        setLoading(false);
        }).catch(error =>{
        console.log(error)
      })
    })

     namespaceList = chartsByNamespace.map((key, value)=>{
      return <MenuItem key={key}>{key}</MenuItem>
    });

    if (isLoading){
      return (
        <div style={{ display: 'flex', height: '100%' }}>
          <Sidebar>
            <Menu>
            </Menu>
          </Sidebar>
          <main>
            <button onClick={() => collapseSidebar()}>Collapse</button>
          </main>
        </div>
      );
    }
    return (
      <div style={{ display: 'flex', height: '100%' }}>
        <Sidebar>
          <Menu>
          {chartsByNamespace.map((key, value)=>{
              <MenuItem key={key}>{key}</MenuItem>
              })
            };
          </Menu>
        </Sidebar>
        <main>
          <button onClick={() => collapseSidebar()}>Collapse</button>
        </main>
      </div>
    );
};

export default Layout;
