import { Sidebar, Menu, MenuItem, useProSidebar } from 'react-pro-sidebar';
import React, {useState, useEffect} from 'react';


const Layout = () => {
    const [chartsByNamespace, setChartsByNamespace] = useState();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {

    const fetchData = () => {
      fetch('http://localhost:8080/api/v1/helm/namespaces')
      .then(response => {
          return response.json()
      })
      .then(data => {
        setChartsByNamespace(data["releases"]);
        console.log(data["releases"]);
        setIsLoading(false);
      })
    };

    fetchData();
  }, []);

    return (
      <div style={{ display: 'flex', height: '100%' }}>
        <Sidebar>
        {isLoading ? (
          <p>loading</p>
        ) : (
          <Menu>
           {Object.entries(chartsByNamespace).map(([namespace, value]) => (
              <MenuItem key={namespace}>{namespace} ({value.length})</MenuItem>
           ))} 
          </Menu>
          )}
        </Sidebar>
      </div>
    );
};

export default Layout;
