import { Sidebar, Menu, MenuItem, useProSidebar, SubMenu } from 'react-pro-sidebar';
import React, {useState, useEffect} from 'react';


const Layout = () => {
    let chartView = null;

    const [chartsByNamespace, setChartsByNamespace] = useState();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {

    const fetchData = () => {
      fetch('http://localhost:8080/api/v1/helm/namespaces')
      .then(response => {
          return response.json()
      })
      .then(data => {
        setChartsByNamespace(data["releases"]);
        console.log(data["releases"]);
        setIsLoading(false);
      })
    };

    fetchData();
  }, []);

  const getNumDeployed = (charts) => {
    let numDeployed = 0;
    charts.map((chart) => {
      if (chart.info.status == "deployed"){
        numDeployed++;
      }
    })

    return numDeployed;
  }

  const handleClick = (chart) => {

  }

  const getNamespaceLabel = (namespace, charts) => {
    let namespaceLabel = namespace;
    namespaceLabel = namespaceLabel + " (" + getNumDeployed(charts) + "/" +charts.length + ")";
    return namespaceLabel;
  }

  const Charts = ({charts}) => (
    <div>
    {/* {charts.map(chart => { */}
        <MenuItem key={charts[0].name} onClick={() => handleClick(charts[0])}>{charts[0].name}</MenuItem>
     {/* })}   */}
    </div>  
  ) 

    return (
      <div style={{ display: 'flex', height: '100%' }}>
        <Sidebar>
        {isLoading ? (
          <p>loading</p>
        ) : (
          <Menu>
           {Object.entries(chartsByNamespace).map(([namespace, charts]) => (
             <SubMenu key={namespace} label={getNamespaceLabel(namespace, charts)}>
              <Charts charts={charts}/>
            </SubMenu>
            ))} 
          </Menu>
          )}
        </Sidebar>
      </div>
    );
};

export default Layout;
