import { Sidebar, Menu, MenuItem, useProSidebar } from 'react-pro-sidebar';
import React, {useState, useEffect} from 'react';


const Layout = () => {
    const { collapseSidebar } = useProSidebar();
    const [chartsByNamespace, setChartsByNamespace] = useState();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {

    const fetchData = () => {
      fetch('http://localhost:8080/api/v1/helm/namespaces')
      .then(response => {
          return response.json()
      })
      .then(data => {
        setChartsByNamespace(data["releases"]);
        console.log(data["releases"]);
        setIsLoading(false);
        console.log("ENTRIES: " + Object.entries(chartsByNamespace));
      })
    };

    fetchData();
  }, []);

    return (
      <div style={{ display: 'flex', height: '100%' }}>
        <Sidebar>
        {isLoading ? (
          <p>loading</p>
        ) : (
             <Menu>
           {Object.entries(chartsByNamespace).map(([namespace, value]) => (
              <MenuItem key={namespace}>{namespace} numCharts: {value.length}</MenuItem>
           ))} 

             </Menu>
          )}
        </Sidebar>
        <main>
          <button onClick={() => collapseSidebar()}>Collapse</button>
        </main>
      </div>
    );
};

export default Layout;
