import { Sidebar, Menu, MenuItem, useProSidebar, SubMenu } from 'react-pro-sidebar';
import React, {useState, useEffect} from 'react';


const Layout = () => {
    const [chartsByNamespace, setChartsByNamespace] = useState();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {

    const fetchData = () => {
      fetch('http://localhost:8080/api/v1/helm/namespaces')
      .then(response => {
          return response.json()
      })
      .then(data => {
        setChartsByNamespace(data["releases"]);
        console.log(data["releases"]);
        setIsLoading(false);
      })
    };

    fetchData();
  }, []);

  const getNumDeployed = (charts) => {
    let numDeployed = 0;
    charts.map((chart) => {
      if (chart.info.status == "deployed"){
        numDeployed++;
      }
    })

    return numDeployed;
  }

    return (
      <div style={{ display: 'flex', height: '100%' }}>
        <Sidebar>
        {isLoading ? (
          <p>loading</p>
        ) : (
          <Menu>
           {Object.entries(chartsByNamespace).map(([namespace, value]) => (
             <SubMenu label={namespace}>
              {/* <MenuItem key={namespace}>{namespace} ({getNumDeployed(value)}/{value.length})</MenuItem> */}
             {value.map((chart) => {
              <MenuItem key={namespace}>{chart.name}t</MenuItem>
             })}

            </SubMenu>
            ))} 
          </Menu>
          )}
        </Sidebar>
      </div>
    );
};

export default Layout;
